import React from 'react';
import './HeroImage.css';

const HeroImage = (props) =>{
    return(
    <div className="rmdb-heroimage"
         style={{
            background:
              `linear-gradient(to bottom, rgba(0,0,0,0)
              39%,rgba(0,0,0,0)
              41%,rgba(0,0,0,0.65)
              100%),
              url('${props.image}'), #1c1c1c`
          }}
         >
        HeroImage
    </div>
    );
};

export default HeroImage;
